var suggestions=document.getElementById('suggestions'),userinput=document.getElementById('userinput');document.addEventListener('keydown',inputFocus);function inputFocus(a){a.keyCode===191&&(a.preventDefault(),userinput.focus()),a.keyCode===27&&(userinput.blur(),suggestions.classList.add('d-none'))}document.addEventListener('click',function(a){var b=suggestions.contains(a.target);b||suggestions.classList.add('d-none')}),document.addEventListener('keydown',suggestionFocus);function suggestionFocus(b){const d=suggestions.querySelectorAll('a'),e=[...d],a=e.indexOf(document.activeElement);let c=0;b.keyCode===38?(b.preventDefault(),c=a>0?a-1:0,d[c].focus()):b.keyCode===40&&(b.preventDefault(),c=a+1<e.length?a+1:a,d[c].focus())}(function(){var b=new FlexSearch.Document({tokenize:"forward",cache:100,document:{id:'id',store:["href","title","description"],index:["title","description","content"]}});b.add({id:0,href:"https://doks-child-theme.netlify.app/docs/prologue/introduction/",title:"Малая Сыя | Гостиный двор ",description:"Doks is a Hugo theme helping you build modern documentation websites that are secure, fast, and SEO-ready — by default.",content:'\u003ch2 id="отдых"\u003eОтдых\u003c/h2\u003e\n\u003cp\u003eК нам погостить заезжают разные туристы – и остаются довольны (возвращаются снова). Одни приезжают семьями на автомобилях, другие – компаниями по пятнадцать – двадцать человек.\u003c/p\u003e\n\u003ch3 id="продолжительность"\u003eПродолжительность\u003c/h3\u003e\n\u003cdiv class="alert alert-warning d-flex" role="alert"\u003e\r\n  \u003cdiv class="flex-shrink-1 alert-icon"\u003e👉 \u003c/div\u003e\r\n  \r\n    \u003cdiv class="w-100"\u003eОбычно останавливаются на три-четыре дня, потому что посещение Малой Сыи для них своего рода обязательный ритуал путешествия по Хакасии, способ разнообразить отдых на озерах Хакасии, порадовать глаз сменой степных пейзажей озер на очарование гор и тайги. \u003c/div\u003e\r\n  \r\n\u003c/div\u003e\r\n\n\u003cp\u003eУ нас вокруг только тишина, горы, река, уютная гостиница, горячая русская баня. Вам обязательно понравится! А от экскурсий впечатлений и положительных эмоций хватит надолго!\u003c/p\u003e\n\u003ch3 id="связь"\u003eСвязь\u003c/h3\u003e\n\u003cdiv class="alert alert-warning d-flex" role="alert"\u003e\r\n  \u003cdiv class="flex-shrink-1 alert-icon"\u003e👉 \u003c/div\u003e\r\n  \r\n    \u003cdiv class="w-100"\u003eОтдельная тема с номерами телефонов, по которым Вы можете позвонить, чтобы узнать ответы на интересующие Вас вопросы. В том числе обо всем, что касается заселения или бронирования мест, лучше писать СМС с просьбой перезвонить, потому что связь в деревне довольно плохая. \u003c/div\u003e\r\n  \r\n\u003c/div\u003e\r\n\n\u003cp\u003e7-913-059-7432 или 7 -913-058-3526\u003c/p\u003e\n\u003ch2 id="как-нас-найти"\u003eКАК НАС НАЙТИ:\u003c/h2\u003e\n\u003cp\u003eЕсли Вы – автотурист, тогда доезжаете до Шира, затем сверните из Шира по трассе на Коммунар. Не доезжая поселка Коммунар следуйте до Малой Сыи (50 км от Шира). Трасса от Шира до Малой Сыи – частично представляет собой гравийное шоссе. Гостиный Двор (магазин \u0026ldquo;Золушка\u0026rdquo;) находится на выезде из посёлка Малая Сыя в сторону Коммунара.\nРасстояние до Малой Сыи составляет:\n• от Томска 700 км., время пути - 8-9 часов.\n• от Красноярска 700 км., время пути - 8-9 часов.\n• от Новосибирска 800 км., время в пути 10 -11 часов\n• от Абакана 300 км., время в пути 5-6 часов\nв том числе, расстояние до поселка Малая Сыя по трассе Шира-Коммунар 50 км.\nВнимание!\nАвтозаправка в Шира.\nДалее на трассе Шира - Коммунар заправок нет!\nДля путешествующих поездом.\nК нам можно добраться поездом Москва-Абакан до станции Шира. Поезд прибывает ночью в 2 часа местного времени. Автостанция рядом с ж/д вокзалом. В 4-30 автобус Шира-Коммунар от автостанции до деревни Малая Сыя (кроме выходных дней). \u0026ldquo;Гостиный двор\u0026rdquo; последнее здание в деревне слева по движению автобуса.\nКак с нами связаться?:\nНепосредственно в деревне «Малая Сыя» сотовая связь не стабильна.\nПоэтому – высылаете СМС на телефоны:\n7-913-059-7432 или 7 -913-058-3526\nс текстом: Перезвоните, есть заявка на Гостиный двор.\nИли с любым другим текстом)))\nПетр Осипович перезвонит вам.\u003c/p\u003e\n\u003ch3 id="в-округе"\u003eВ округе:\u003c/h3\u003e\n\u003cp\u003eВокруг посёлка достаточно несложных, красивых маршрутов для самостоятельных горно-пешеходных прогулок.\u003c/p\u003e\n\u003ch3 id="времена-года"\u003eВремена года:\u003c/h3\u003e\n\u003cp\u003eОсень и весна на «Малой Сые» - очаровательны. Отдых в это время года подходит для тех, кто хочет отдохнуть от суеты городов. Здесь нет ни интернета, ни сотовой связи, телевидение – и то один канал. Вокруг только тишина и первозданная природа в сочетании с комфортными условиями проживания наших гостей.\nВсе это подходит для тех, кто способен оценить красоту природы, любит провести время в поисках пейзажей, чтобы запечатлеть на фотокамеру. Непременно испытают удовольствие от о отдыха и те, кто хочет пообщаться с друзьями – выехать на природу на пикник, погулять по тропам в горах, сходить в пещеры, попариться в баньке.\u003c/p\u003e\n\u003ch3 id="осень"\u003eОсень.\u003c/h3\u003e\n\u003cp\u003eИтак, осень… Для Хакасии свойственна сухая, продолжительная золотая осень. Обычно в сентябре стоит солнечная погода – бабье лето в разгаре. Покрытые, преимущественно лиственницами и березами горы, полыхают золотыми красками, а в сочетании с ярко красными осинами, и темно-зелеными елями – от тайги просто не оторвать глаз. Рекомендуем нашим гостям обязательно побывать в Хакасии в сентябре. В это время года доступны также все экскурсии.\u003c/p\u003e\n\u003ch3 id="зима"\u003eЗима.\u003c/h3\u003e\n\u003cp\u003eЗима. Как всякая сибирская зима - холодная, с трескучими морозами. Пока солнце вскарабкается из-за гор - едва начинает пригревать к 11-ти часам дня, а к пяти вечера снова прячется за гору. Однако всегда находятся любители встретить Новый год веселой компанией, тем более, что для походов в пещеру время года не имеет значения, там и зимой и летом температура одинаковая ( + 4-6 градусов).\u003c/p\u003e\n\u003ch2 id="весна"\u003eВесна.\u003c/h2\u003e\n\u003cp\u003eВесна. В холодное время года для отдыха нашим гостям рекомендуем период, начиная с марта – для нашей местности свойственна солнечная погода, располагающая к прогулкам на природе. А на майские праздники снега уже практически нет, можно полноценно отдохнуть на природе, созерцая цветение подснежников и набирающие силы краски природы.\u003c/p\u003e\n\u003ch2 id="экскурсии"\u003eЭкскурсии:\u003c/h2\u003e\n\u003cp\u003eПещера\n\u0026ldquo;Археологическая\u0026rdquo;\n1,5 - 2 часа\nПосещение с инструктором,\nснаряжение выдается. 250 руб/чел\nПещера доступна для осмотра без специальной подготовки, без ограничения возраста при нормальном состоянии здоровья.\u003c/p\u003e\n\u003cp\u003eПещера\n\u0026ldquo;Кирилловская\u0026rdquo;\n4 часа\nПосещение с инструктором,\nснаряжение выдается. 250 руб/чел Имеет протяжённость 320 м при глубине 36 м. Была открыта в р-не пос. «Малая Сыя» одной из первых, но очень хорошо сохранилась в первозданной красоте. Крутой входной спуск оборудован деревянной лестницей, по которой может спуститься любой турист.\u003c/p\u003e\n\u003cp\u003e\u0026ldquo;Сундуки\u0026rdquo;,\nастрологическая\nобсерватория древних\nлюдей.\nДоставка а/м УАЗ. 6-7 часов поездка на а/м УАЗ 2 500 руб./группа на 6-7 человек Своеобразный горный массив - \u0026ldquo;Сибирские пирамиды\u0026rdquo; интересное природное явление. Установив дополнительные элементы, древние люди определяли характер движения светил на небосводе для составления астрологических прогнозов. Материал подготовлен по работам СО РАН академика Ларичева В.Е. http://www.vodnik.narod.ru/hakasia/sunduk.htm\u003c/p\u003e\n\u003cp\u003eСплав\nпо реке Белый Июс\n2-3 часа 250 - 300 руб/чел Сплав на надувных 2 - 4 местных резиновых лодках. Сплав самостоятельный, некатегорийный, порогов, водопадов нет. Уединение с природой, шум реки на перекатах, чистейшая прозрачная вода, где виден каждый камешек на дне, экзотические виды по берегам.\u003c/p\u003e\n'}).add({id:1,href:"https://doks-child-theme.netlify.app/docs/prologue/",title:"Пролог",description:"Prologue Doks.",content:""}).add({id:2,href:"https://doks-child-theme.netlify.app/docs/",title:"Docs",description:"Docs Doks.",content:""}),userinput.addEventListener('input',c,!0),suggestions.addEventListener('click',e,!0);function c(){var h=this.value,f=b.search(h,{limit:5,index:["content"],enrich:!0}),g=suggestions.childNodes,e=0,i=f.length,c;for(suggestions.classList.remove('d-none'),f.forEach(function(b){c=document.createElement('div'),c.innerHTML='<a href><span></span><span></span></a>',a=c.querySelector('a'),t=c.querySelector('span:first-child'),d=c.querySelector('span:nth-child(2)'),a.href=b.result[e].doc.href,t.textContent=b.result[e].doc.title,d.textContent=b.result[e].doc.description,suggestions.appendChild(c)});g.length>i;)suggestions.removeChild(g[e])}function e(){while(suggestions.lastChild)suggestions.removeChild(suggestions.lastChild);return!1}})()